typeProperties.script:
section Section1;
shared DelimitedText4 = let AdfDoc = AzureStorage.BlobContents("https://njlabfiles.blob.core.windows.net/babs/data/201508_station_data.csv"),Csv = Csv.Document(AdfDoc, [Delimiter = ",", Encoding = TextEncoding.Utf8, QuoteStyle = QuoteStyle.Csv]), PromotedHeaders = Table.PromoteHeaders(Csv, [PromoteAllScalars = true]) in  PromotedHeaders;
shared UserQuery = let Source = #"DelimitedText4",
  #"Changed column type" = Table.TransformColumnTypes(Source, {{"installation", type date}}),
  #"Inserted month name" = Table.AddColumn(#"Changed column type", "Month name", each Date.MonthName([installation]), type nullable text),
  #"Changed column type 1" = Table.TransformColumnTypes(#"Inserted month name", {{"dockcount", Int64.Type}}),
  #"Grouped rows" = Table.Group(#"Changed column type 1", {"Month name", "landmark"}, {{"stationCount", each Table.RowCount(_), Int64.Type}, {"TotalDocks", each List.Sum([dockcount]), type nullable text}}) in #"Grouped rows";
